    private void saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveActionPerformed
        c.address=address.getText();
        c.city=city.getSelectedItem().toString();
        c.contact=contact.getText();
        c.dob=dob.getText();
        c.email=email.getText();
        c.fname=fathersname.getText();
        c.gender=gender.getSelectedItem().toString();
        c.idno=idno.getText();
        c.idproof=idproof.getSelectedItem().toString();
        c.lname=lastname.getText();
        c.mstatus=marital.getSelectedItem().toString();
        c.name=firstname.getText();
        c.nationality=nationality.getSelectedItem().toString();
        c.state=state.getSelectedItem().toString();
        c.title=title.getSelectedItem().toString();
        database.createCustomer(c);

        int id=0;
        List<customerbin> cc=database.getAllcustomerByName(c);
        for(customerbin cb:cc){
            id=cb.getCustomerid();
            JOptionPane.showMessageDialog(this,"Your Customer ID is:"+id," Registration ID" , JOptionPane.INFORMATION_MESSAGE);
        }

        roompage2 r=new roompage2();
        r.setBounds(0, 0, 1500, 800);
        r.setVisible(true);
        this.hide();
    }//GEN-LAST:event_saveActionPerformed

