    private void searchKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_searchKeyReleased

        e.setName(search.getText());
        List<employeebin> em=database.getAllemployeeByName(e);
        for(employeebin eb:em){
            
         if(eb.getTitle().equals("Mr.")){
             title.setSelectedItem("Mr.");
         }   
         else  if(eb.getTitle().equals("Mrs.")){
             title.setSelectedItem("Mrs.");
         }
         else
         {
            if(eb.getTitle().equals("Miss")){
             title.setSelectedItem("Miss");
         }
         }
             
         firstname.setText(eb.getName());
         lastname.setText(eb.getLname());
         fathersname.setText(eb.getFname());
         
          if(eb.getGender().equals("Male")){
             gender.setSelectedItem("Male");
         } 
          else if(eb.getGender().equals("Female")){
             gender.setSelectedItem("Female");
         } 
         dob.setText(eb.getDob());
         contact.setText(eb.getContact());
         
          if(eb.getMstatus().equals("Single")){
             marital.setSelectedItem("Single");
         } 
          else {
             marital.setSelectedItem("Married");
         }
            emailid.setText(eb.getEmail());
            address.setText(eb.getAddress());
            
             if(eb.getIdproof().equals("Aadhar Card")){
             idproof.setSelectedItem("Aadhar Card");
         } 
          else if(eb.getIdproof().equals("Driving Licence")){
             idproof.setSelectedItem("Driving Licence");
         } 
          else if(eb.getIdproof().equals("Pan Card")){
             idproof.setSelectedItem("Pan Card");
         } 
          else if(eb.getIdproof().equals("Passport")){
             idproof.setSelectedItem("Passport");
         } 
         else if(eb.getIdproof().equals("Voter ID")){
             idproof.setSelectedItem("Voter ID");
         } 
          else {
             idproof.setSelectedItem("Others");
         } 
             idno.setText(eb.getIdno());
             password.setText(eb.getPassword());
        }
    }//GEN-LAST:event_searchKeyReleased

